mapper.select:
select* from tb_review
where p_idx= #{p_idx}

	
		ORDER BY post_date ASC;
	
	
		ORDER BY post_date DESC;
	
	
		ORDER BY star ASC;
	
	
		ORDER BY star DESC;

choose.when:
insert into tb_pvoc (m_idx, p_idx, product_name, writer, email, content, originfile_name, savefile_name, voc_state)
	values(#{m_idx},#{p_idx},#{product_name},#{writer},#{email},#{content},#{originfile_name}
	,#{savefile_name},#{voc_state})

mapper.select:
SELECT * FROM tb_product WHERE m_idx LIKE #{m_idx}
ORDER BY p_last_modified_date DESC
LIMIT 20 OFFSET 0

mapper.update:
	UPDATE tb_cart
    SET payment_or_not = #{payment_or_not}
    WHERE p_idx = #{p_idx}

mapper.select:
select* from tb_review
where m_idx= #{m_idx}
ORDER BY post_date ASC
LIMIT 20 OFFSET 0;

select * from tb_cart where p_idx = #{p_idx}
					  and m_idx = #{m_idx}
					  and payment_or_not = 0

mapper.update:
update tb_cart set c_quantity=#{c_quantity}
	where m_idx=#{m_idx}
	and p_idx=#{p_idx}
	and payment_or_not = 0

mapper.delete:
delete from tb_cart 
		where m_idx=#{m_idx}
		and p_idx=#{p_idx}
		and payment_or_not = 0

mapper.update:
update tb_cart set payment_or_not=1
	where m_idx=#{m_idx}
	and payment_or_not=0
