mapper.select:
SELECT * FROM tb_product WHERE category LIKE #{category}
ORDER BY p_idx DESC
LIMIT 20 OFFSET 0

select a.c_idx, a.m_idx, a.c_quantity, b.p_idx, b.product_name, b.price, b.discount_rate
from tb_cart a left outer join tb_product b on a.p_idx = b.p_idx 
where a.m_idx = #{m_idx}

mapper.update:
update tb_cart set c_quantity=#{c_quantity}
	where m_idx=#{m_idx}
	and p_idx=#{p_idx}

mapper.delete:
delete from tb_cart 
		where m_idx=#{m_idx}
		and p_idx=#{p_idx}

mapper.select:
select a.o_idx, a.m_idx,a.c_quantity, b.p_idx, b.price, b.discount_rate
from tb_order a left outer join tb_product b on a.p_idx = b.p_idx 
where a.m_idx = #{m_idx}
